<?php get_header(); ?><div id="mainwrapper" class="clearfix">    <div id="maincontent">        <?php if (have_posts()) : ?>            <?php while (have_posts()) : the_post(); ?>                <div class="post">                    <div class="posthit" onclick="location.href='<?php the_permalink() ?>'">                        <h2 id="post-<?php the_ID(); ?>" class="thetitle"><?php the_title(); ?></h2>                        <div class="postinfo"><?php the_time('F jS, Y') ?> by <?php the_author() ?></div>                        <div class="entry">                            <?php /*?><?php the_content(''); ?><?php */?>                            <?php the_excerpt(); ?>                            <a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title(); ?>" class="readmore">Read the Entire Post ></a>                        </div>                        <p class="postmetadata">                        	<strong>Posted in:</strong> <?php the_category(' | ') ?><br />                            							<?php if ('open' == $post-> comment_status) : ?>                            	<strong>Interact:</strong>                             	<?php comments_popup_link('Add a Comment', 'View 1 Comment / Add a Comment', 'View % Comments / Add a Comment'); ?><br />                            <?php else : // comments are closed ?>                            	<!--Comments are closed-->                            <?php endif; ?>                            <strong>Share:</strong>                             <a href="http://digg.com/submit?phase=2&url=<?php echo get_permalink() ?>&title=<?php the_title(); ?>" title="Digg this post on digg.com.">digg</a> |                             <a href="http://del.icio.us/post?url=<?php echo get_permalink() ?>&title=<?php the_title(); ?>" title="Bookmark this post on del.icio.us.">del.icio.us</a> |                             <a href="http://technorati.com/faves?sub=addfavbtn&amp;add=<?php echo get_permalink() ?>" title="Add To Technorati Favorites">Technorati</a><br />                        </p>                        <!--                        <?php trackback_rdf(); ?>                        -->                    </div>                </div>            <?php endwhile; ?>                        <?php 			$max_num_pages = $wp_query->max_num_pages;			if($max_num_pages > 1) { 			?>			<div class="prevnextnavigation clearfix">                <div class="alignleft"><?php posts_nav_link('','','&laquo; Previous Entries') ?></div>                <div class="alignright"><?php posts_nav_link('','Next Entries &raquo;','') ?></div>            </div>			<?php }else{ } ?>		        <?php else : ?>            <div class="post">                <div class="postnohit">                    <h2 class="pagetitle">Not Found</h2>                </div>            </div>        <?php endif; ?>    </div><!-- close of maincontent --></div><!-- close of mainwrapper --><?php get_footer(); ?>